    result = col.find_one({"id": request.args.get("id")})
    return jsonify(result)
@app.route("/retrieval/<id>")
def retrieval(id):
    result = col.find_one({"id": id})
    return jsonify(result)
if __name__ == "__main__":
    app.run(debug=True)
```
